sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  var testReleasePhase_sysID = inputs["TestReleasePhase"];
	var SelectedSystemTypeVar = inputs["SelectedSystemType"];
 // var SelectedSystemTypeVar = 'tcapp';

  //var ifOutput = "false".toString();
  var ifOutput = false;

 var inputSystemType  = "";//testReleasePhase_sysID.x_675641_sbd_relea_release.x_675641_sbd_relea_system_type;



//  gs.info('SBD inputs For typeof inputSystemTypeSTRING----'+ inputSystemTypeString);
   var SelectedSystemTypeVarString = '';
 //  gs.info('SBD inputs For typeof SelectedSystemTypeVar----'+ typeof SelectedSystemTypeVar);
  SelectedSystemTypeVarString  = SelectedSystemTypeVar.toString();
//  gs.info('SBD inputs For typeof SelectedSystemTypeVarSTRING----'+ SelectedSystemTypeVarString);

//var  inputSystemType = 'tcapp,infrastructure';
 var gr = new GlideRecord('rm_release');
  gr.addQuery('sys_id',testReleasePhase_sysID.x_675641_sbd_relea_release);
  gr.query()
  if(gr.next()){
  inputSystemType = gr.x_675641_sbd_relea_system_type;

     gs.info('SBD inputs For typeof inputSystemType----'+ typeof inputSystemType.toString()+'----'+inputSystemType);
      var inputSystemTypeString = inputSystemType.toString();
    var indexVal = inputSystemTypeString.indexOf(SelectedSystemTypeVarString.trim());
   gs.info('SBD inputs indexVal----'+indexVal);
    if(indexVal>-1){
  //  ifOutput = "true".toString();
      ifOutput = true;
    }
  }




 //   ifOutput = "true";
     //    gs.info('SBD inputs IFff inputSystemTypeSplit----'+inputSystemTypeSplit[i].toString()+'------'+ifOutput);

  outputs["ifresult"] = ifOutput;




})(inputs, outputs);

