sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	//Question-30 Are systems scanned for vulnerabilities? YES
	//Ques Question-30.1 - Is the application codebase available/accessible to the Project team? If yes, SAST Scan == True
	//Question-30.2 Does the application leverage open source components/libraries? If yes, SCA Scan == True
	//Question-16 Are all users of the system identified and authenticated? YES
	//Question-16.1 If yes, is Client SSO being used? If yes, SSO Pattern== True
	//Question-11 Are audit creation, protection and storage capabilities in place?  YES 
	//Question-11.1 If yes, are all access attempts and administrative activities logged? YES || NO Logging Pattern == True
	//Question-11.1.1 Are logs stored on a central server? YES|| NO Logging Pattern == True
	//Question-42 Are systems/applications in the project accesible to remote users inside or outside of the organization's internal network? If YES, Remote Access Pattern == True
	//Question-8 Is the data encrypted to protect it during remote sessions and wireless access? NO ||YES Rest Encryption Pattern == True
	//Question-2 Is system access restricted to only permitted activities and functions? YES
	//Question-2.1 Is there a formal user provisioning and deprovisioning process in place? Answer is YES||NO, Active Directory Pattern == True

	var RelPhase_resultSrc = current.trigger_id+'';
	var assmet_instance = current.sys_id+'';
	var ret_result_obj = [] ;
	var Rel_sys_id = '';
	var grRelPhase = new GlideRecord("rm_release_phase");
grRelPhase.addQuery("sys_id", RelPhase_resultSrc);
grRelPhase.query();
if (grRelPhase.next()) {
   Rel_sys_id = grRelPhase.x_675641_sbd_relea_release+'';
}

var ques = ['Question-30.1','Question-30.2','Question-16.1','Question-11.1.1','Question-42','Question-8','Question-2.1'];
for(var i=0;i<ques.length;i++){
//source_id=1c19bea0975f11100406bbc3f153afb9^instance=9419fea0975f11100406bbc3f153afb3
var result_query = 'source_id='+RelPhase_resultSrc+'^instance='+''+assmet_instance+'^metric.name='+ques[i];
var gr = new GlideRecord("asmt_metric_result");
gr.addEncodedQuery(result_query);
gr.query();
if (gr.next()) {
   ret_result_obj.push([gr.metric.name,gr.actual_value]);
}
}
gs.info('SBD SAST'+ret_result_obj[1][0]);

var mapping = [['Question-30.1','x_675641_sbd_relea_sast_scan'],['Question-30.2','x_675641_sbd_relea_sca_scan'],['Question-16.1','x_675641_sbd_relea_sso_pattern'],['Question-11.1.1','x_675641_sbd_relea_logging_pattern'],['Question-42','x_675641_sbd_relea_remote_access_pattern'],['Question-8','x_675641_sbd_relea_rest_encryption_pattern'],['Question-2.1','x_675641_sbd_relea_active_directory_pattern']];

gs.info('SBD SAST'+mapping.length);

for(var j=0;j<ret_result_obj.length;j++){
if(ret_result_obj[j][0] == mapping[j][0])
{
gs.info(ret_result_obj[j][0]+'----------'+mapping[j][0]);
var gr1  = new GlideRecord('rm_release');
gr1.addQuery('sys_id',Rel_sys_id);
gr1.query();
if(gr1.next())
{
//gs.info('Hello');
if(ret_result_obj[j][1] == '1'){
gr1[mapping[j][1]] = 'true';
gr1.update();
gs.info('SBD SAST'+ret_result_obj[j][1]+'----------'+mapping[j][1]);
}

}

}
}


})(current, previous);
