sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    var releasePhase = current.trigger_id.sys_id;
    var metricType = current.metric_type.getDisplayValue();

	gs.info('aaaaaaaaaaa'+metricType);
    if (metricType == 'SbD assessment-High' || metricType == 'SbD assessment- Medium' || metricType == 'SbD assessment-Low') {
        var releaseTask = new GlideRecord('rm_task');
        releaseTask.addEncodedQuery('parent.sys_idSTARTSWITH' + releasePhase);
        releaseTask.query();
        while (releaseTask.next()) {
            var subType = releaseTask.x_675641_sbd_relea_sub_type;
            gs.info('sssssssssss111' + releaseTask);
            if (subType == 'complete security assessment') {
                releaseTask.x_675641_sbd_relea_status = 'completed';
                releaseTask.state = 3;
                releaseTask.update();
            }
        }

    } 

	else if (metricType == 'Production Baseline Assessment') {
        var releaseTask1 = new GlideRecord('rm_task');
        releaseTask1.addEncodedQuery('sys_id',releasePhase);
        releaseTask1.query();
        while (releaseTask.next()) {
            var subType1 = releaseTask1.x_675641_sbd_relea_sub_type;
            gs.info('sssssssssss222' + releaseTask1);
            if (subType1 == 'complete baseline assessment') {
                releaseTask1.x_675641_sbd_relea_status = 'completed';
                releaseTask1.state = 3;
                releaseTask1.update();
            }
        }
    }





})(current, previous);
