sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here

	var assignto = current.assigned_to.sys_id;
	var tablesys = current.sys_id;
	var base_type = 'adbc1c1e975311100406bbc3f153af36';
    var base_category = '2cfc545e975311100406bbc3f153af2a';

	var ga = new GlideAggregate(current.getTableName());
        ga.addQuery('sys_id', tablesys);
        ga.addAggregate('COUNT');
        ga.query();
        if (ga.next() && ga.getAggregate('COUNT') > 0)
            new global.AssessmentUtils().checkRecord(current, base_type);

	var assessable = new GlideRecord('asmt_assessable_record');
        assessable.addQuery('source_id', tablesys);
        assessable.addQuery('metric_type', base_type);
        assessable.query();
        assessable.next();

	var catRecord = new GlideRecord('asmt_m2m_category_assessment');
        catRecord.initialize();
        catRecord.category = base_category;
        catRecord.assessment_record = assessable.sys_id;
        catRecord.insert();

	var assessment = (new global.AssessmentUtils().createAssessments(base_type, tablesys, assignto));
        var assessmentID = (assessment.split(','))[0];

	var task = new GlideRecord('asmt_assessment_instance');
        task.get(assessmentID);
        task.task_id = tablesys;
		task.metric_type = base_type;
        task.trigger_id = tablesys;
        task.user = assignto;
        task.trigger_table = current.getTableName();
        task.update();


})(current, previous);
