sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {


   //Type appropriate comment here, and begin script below
  if((newValue=='aws'))
	  {
		  g_form.setDisplay('x_675641_sbd_relea_access_key',true);
		  g_form.setMandatory('x_675641_sbd_relea_access_key',true);
		  g_form.setDisplay('x_675641_sbd_relea_vpc_id',true);
		  g_form.setMandatory('x_675641_sbd_relea_vpc_id',true);
		  g_form.setDisplay('x_675641_sbd_relea_secret_key',true);
		  g_form.setMandatory('x_675641_sbd_relea_secret_key',true);
		  g_form.setDisplay('x_675641_sbd_relea_aws_account_id',true);
		  g_form.setMandatory('x_675641_sbd_relea_aws_account_id',true);
	  }
	else
		{
			g_form.setMandatory('x_675641_sbd_relea_access_key',false);
			g_form.setMandatory('x_675641_sbd_relea_vpc_id',false);
			g_form.setMandatory('x_675641_sbd_relea_secret_key',false);
			g_form.setMandatory('x_675641_sbd_relea_aws_account_id',false);
			g_form.setDisplay('x_675641_sbd_relea_access_key',false);
			g_form.setDisplay('x_675641_sbd_relea_vpc_id',false);
			g_form.setDisplay('x_675641_sbd_relea_secret_key',false);
			g_form.setDisplay('x_675641_sbd_relea_aws_account_id',false);
		}
}
