function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	//g_form.addInfoMessage(newValue);
   if((newValue=='true') && (g_form.getValue('x_675641_sbd_relea_sub_type')=='create_threat_model') && (newValue!=''))
	   {
		   g_form.setDisplay('x_675641_sbd_relea_aws_or_azure',true);
		   g_form.setMandatory('x_675641_sbd_relea_aws_or_azure',true);
	   }
	else
		{
			g_form.setMandatory('x_675641_sbd_relea_aws_or_azure',false);
			g_form.setDisplay('x_675641_sbd_relea_aws_or_azure',false);
		}
}
