sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    var Reltask_resultSrc = current.trigger_id + '';
    var assmet_instance = current.sys_id + '';
    var Reltask_RElease = '';


    //GPS Control Databse - Production control assessment
    //================================
    var ret_result_obj = [];
    var ques_field_map = [];
    var ques = ['Question 6', 'Question 13', 'Question 8.1', 'Question 15', 'Question 19', 'Question 20', 'Question 19.2.1', 'Question 22', 'Question 24', 'Question 27.1'];
    for (var i = 0; i < ques.length; i++) {
        var result_query = 'source_id=' + Reltask_resultSrc + '^instance=' + '' + assmet_instance + '^metric.name=' + ques[i] + '^actual_value!=-1^ORactual_value=NULL';
        //var result_query = 'source_id='+'ef355fb5971b51100406bbc3f153afc2'+'^instance='+'fb351fb5971b51100406bbc3f153afe3'+''+'^metric.name='+ques[i]+'^actual_value!=-1^ORactual_value=NULL';
        var grResult = new GlideRecord("asmt_metric_result");
        grResult.addEncodedQuery(result_query);
        grResult.query();
        if (grResult.next()) {
            ret_result_obj.push([grResult.metric.name, grResult.actual_value]);
        }
    }
    gs.info('1. SBP Prod Baseline======='+ret_result_obj);
    ////Question 6,1,Question 13,2,Question 8.1,3,Question 15,-1,Question 19,-1,Question 20,-1,Question 22,-1,Question 24,-1

    ques_field_map = [
        ['Question 6', 'x_675641_sbd_relea_single_sign_on_sso'],
        ['Question 13', 'x_675641_sbd_relea_role_based_access_control_rbac'],
        ['Question 8.1', 'x_675641_sbd_relea_multi_factor_authentication_mfa'],
        ['Question 15', 'x_675641_sbd_relea_privileged_access_management_pam'],
        ['Question 19', 'x_675641_sbd_relea_central_application_logging'],
        ['Question 20', 'x_675641_sbd_relea_encryption_at_rest'],
        ['Question 19.2.1', 'x_675641_sbd_relea_monitoring_alerting'],
        ['Question 22', 'x_675641_sbd_relea_encryption_in_transit'],
        ['Question 24', 'x_675641_sbd_relea_vulnerability_testing'],
        ['Question 27.1', 'x_675641_sbd_relea_mobile_device_management_mdm']
    ];



    var grRelTAsk = new GlideRecord("rm_task");
    grRelTAsk.addQuery("sys_id", Reltask_resultSrc);
    grRelTAsk.query();
    if (grRelTAsk.next()) {
        Reltask_RElease = grRelTAsk.parent.parent + ''; //to get the release of the task



        var grREL = new GlideRecord("rm_release");
        grREL.addQuery("sys_id", Reltask_RElease);
        grREL.query();
        if (grREL.next()) {

            for (var j = 0; j < ret_result_obj.length; j++) {
                if (ret_result_obj[j][0] == ques_field_map[j][0]) {
                   gs.info('2. SBP Prod Baseline======='+ret_result_obj[j][1] + '===========' + ques_field_map[j][1] + '=============' + ret_result_obj[j][0]);
                    //x_675641_sbd_relea: Question 6,1,Question 13,2,Question 8.1,3
                    //x_675641_sbd_relea: 1===========x_675641_sbd_relea_single_sign_on_sso=============Question 6
                    //x_675641_sbd_relea: 2===========x_675641_sbd_relea_role_based_access_control_rbac=============Question 13
                    //x_675641_sbd_relea: 3===========x_675641_sbd_relea_multi_factor_authentication_mfa=============Question 8.1

                    grREL[ques_field_map[j][1]] = ret_result_obj[j][1] + '';
                }
            }
            grREL.update();
        }
    }

})(current, previous);
