sys_script_include.script:
var Cloud_security_controlscreation = Class.create();
Cloud_security_controlscreation.prototype = {
    initialize: function() {
    },

	_create_cloud_security_controls : function(components,releasePhase)
	{
	var findComponent = new GlideRecord('sn_compliance_policy_statement');
	findComponent.addQuery('name','IN',components);
	findComponent.query();
	while(findComponent.next())
		{
			var createCsb = new GlideRecord('sn_compliance_control');
			createCsb.initialize();
			createCsb.x_675641_sbd_relea_release = releasePhase;
			createCsb.content = findComponent.sys_id;
			createCsb.x_675641_sbd_relea_cloud_security_control = true;
			createCsb.insert();
		}	
	},

    type: 'Cloud_security_controlscreation'
};
