sys_script_include.script:
var SBDTaskUtil = Class.create();
SBDTaskUtil.prototype = {
    initialize: function() {},

    createScanTASK: function(scanType, relPhase) {
        var scanTASKDEsc = '';
        var scanTASksubType = '';
        if (scanType == 'SAST') {
            scanTASKDEsc = 'Perform the SAST Scan and update the respective questionnaire.';
            scanTASksubType = 'sast scan';
        }
        if (scanType == 'SCA') {
            scanTASKDEsc = 'Perform the SCA Scan and update the respective questionnaire.';
            scanTASksubType = 'sca scan';
        }

        var sbdSCanTask = new GlideRecord("rm_task");
        sbdSCanTask.initialize();

        sbdSCanTask.description = scanTASKDEsc;
        sbdSCanTask.parent = relPhase.sys_id;
        sbdSCanTask.x_675641_sbd_relea_release_phase = relPhase.sys_id;
        sbdSCanTask.x_675641_sbd_relea_type = 'security_testing';
        sbdSCanTask.x_675641_sbd_relea_sub_type = scanTASksubType;
        sbdSCanTask.state = '1';
        sbdSCanTask.x_675641_sbd_relea_status = 'ready_to_take';
        sbdSCanTask.assigned_to = relPhase.x_675641_sbd_relea_release.x_675641_sbd_relea_primary_build_contact + '';
        sbdSCanTask.x_675641_sbd_relea_sbd_release_task = 'true';
        sbdSCanTask.insert();


    },
    type: 'SBDTaskUtil'
};
