sys_script_include.script:
var Create_PCI_PII_controls = Class.create();
Create_PCI_PII_controls.prototype = {
    initialize: function() {
    },

	_createPCIPII: function(taskID)
	{
		var findRelease = new GlideRecord('rm_release_phase');
		findRelease.addQuery('sys_id',taskID);
		findRelease.query();
		while(findRelease.next())
			{
				var PCI = findRelease.parent.x_675641_sbd_relea_pci;
				var PII = findRelease.parent.x_675641_sbd_relea_pii;
				var releasephasesysid = findRelease.sys_id;
				var product = findRelease.parent.x_675641_sbd_relea_product.sys_id;

				if( PCI == 'yes')
					{
						this.createPCI(releasephasesysid,product);
					}
				else if (PII == 'yes')
					{
						this.createPII(releasephasesysid,product);
					}

			}
	},

	createPII : function(releasephasesysid,product)
	{
		var contObj = new GlideRecord('sn_compliance_policy_statement');
		contObj.addEncodedQuery('referenceINSI-12(1),PM-25');
		contObj.query();
		while(contObj.next())
			{
				var createPIIcontrol = new GlideRecord('sn_compliance_control');
				createPIIcontrol.initialize();
				createPIIcontrol.content = contObj.sys_id;
				createPIIcontrol.x_675641_sbd_relea_release = releasephasesysid;
				createPIIcontrol.profile = product;
				createPIIcontrol.insert();
			}
	},

	createPCI : function(releasephasesysid,product)
	{
		var fcontObj = new GlideRecord('sn_compliance_policy_statement');
		fcontObj.addEncodedQuery('referenceINAC-3(11),CM-8, CM-12, CM-13, PM-5');
		fcontObj.query();
		while(fcontObj.next())
			{
				var createPCIcontrol = new GlideRecord('sn_compliance_control');
				createPCIcontrol.initialize();
				createPCIcontrol.content = fcontObj.sys_id;
				createPCIcontrol.x_675641_sbd_relea_release = releasephasesysid;
				createPCIcontrol.profile = product;
				createPCIcontrol.insert();
			}

	},

    type: 'Create_PCI_PII_controls'
};
